package com.AssignOpr;

public class Arithmatic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10;
		int y = 20;
		int res = x + y;
		
		System.out.println("x + y = " + res);
		res = x - y;
		
		System.out.println("x - y = " + res);
		res = x*y;
		
		System.out.println("x * y = " + res);
		res = y/x;
		
		System.out.println("y / x = " + res);
		res = x%3;
		
		System.out.println("x % 3 = " + res);
		
	}

}
