package com.AssignOpr;

public class AssignmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 4;
		int var;
		
		var = a;
		System.out.println("Var using =: "+ var);
		
		var += a;
		System.out.println("Var using =: "+ var);
		
		var *= a;
		System.out.println("Var using =: "+ var);
	}

}
